import React, { Component } from 'react';
import Axios from 'axios';
import api from '../../api'
import { Redirect } from 'react-router-dom'

export default class Home extends Component {

  componentDidMount() {
    Axios,get("")
  }

  logIn = () => {
    if(!api.isLoggedIn()){ //DO THIS ONE
      return (
        <Redirect to="/login" />
      )
    }
  }

  checkUserType = () => {
    if(this.props.user.artistType == "band") {
      return <h1>You are a band</h1>
    }
    else {
      return <h1>You are a musician</h1>
    }
  }

  render() {    
    return (  
      <div className="Home">
        {this.logIn()}
        {this.checkUserType()}
      </div>
    );
  }
}
