import React, { Component } from 'react';
import Axios from 'axios';
import api from '../../api'
import { Redirect, NavLink } from 'react-router-dom'

export default class Home extends Component {

  state = {
    artistType: localStorage.artistType,
    allBands: []
  }
  componentDidMount() {
    this.getBands() //calls once 
  }

  logIn = () => {
    if(!api.isLoggedIn()){ //DO THIS ONE
      return (
        <Redirect to="/login" />
      )
    }
  }

  getBands = () => {
    Axios.get("http://localhost:5000/api/all-bands")
      .then(res => {
        let bandsArray = res.data.allBands
        // console.log(bandsArray)
        this.setState({
          allBands:res.data.allBands
        })
      })
  }

  showData = () => {
    console.log(this)
    if(this.state.artistType === 'band'){
      return (
              <div class="container">
                <div class="row">
                  <div class="col-lg-4">
                    <img src="../../images/guitarist.png" alt="guitarist" />
                    <NavLink to="/musicians/guitar">Guitar Players</NavLink>
                  </div>
                  <div class="col-lg-4">
                    <img src="../../images/singer.png" alt="singer" />
                    <NavLink to="/musicians/guitar">Singers</NavLink>
                  </div>
                  <div class="col-lg-4">
                    <img src="../../images/drummer.png" alt="drummer"/>
                    <NavLink to="/musicians/guitar">Drummers</NavLink>
                  </div>
                </div>
              </div>
            )
    }
    else {
      let showBands = this.state.allBands.map( (band) => {
        return  <div className="col-xl-4 col-lg-4 band-box">
                  <img src="../../images/band-icon.jpg" className="band-image" alt="alt" />
                  <p>{band.username}</p>
                </div>
      })
      return showBands;
    }
  }

  render() {    
    return (  
      <div className="Home">
        {this.logIn()}
        <div className="container">
          <div className="row">
            {this.showData()}
          </div>
        </div>

      </div>
    );
  }
}
