import React, { Component } from 'react';
import Axios from 'axios';
import api from '../../api'
import { Redirect } from 'react-router-dom'

export default class Home extends Component {

  state = {
    artistType: localStorage.artistType,
    musiciansArray: []
  }

  componentDidMount() {
    this.getMusicians()
    // console.log('idk')
    // if(this.state.artistType === "band"){
    //   this.getMusicians()
    // }
    // else {
    //   this.getBands()
    // }
  }

  logIn = () => {
    if(!api.isLoggedIn()){ //DO THIS ONE
      return (
        <Redirect to="/login" />
      )
    }
  }

  getMusicians = () => {
   Axios.get("http://localhost:5000/api/all-musicians")
      .then(res => {
        let musiciansArray = res.data.allMusicians
        this.setState({
          musiciansArray
        })
      })
  }

  showMusicians = () => {
    let result = this.state.musiciansArray.map((musician,i) => {
      return (<p key={i}> {musician.username}</p>)
    })
    return result
  }

  getBands = () => {
    Axios.get("http://localhost:5000/api/all-bands")
      .then(res => {
          console.log(res.data)
          res.data.allBands.map((band,i) => {
            return <p>{band.username}</p>
          })
      })
  }

  render() {    
    return (  
      <div className="Home">

        {this.logIn()}
        {this.showMusicians()}
      </div>
    );
  }
}
